SELECT T0.business_id AS WABusinessId, T0.salesforce_account_id, A.OwnerId, u.Name AS OwnerName, u1.Name AS OBName, A.pISU__c AS ImpDate, T0.name, A.TC_Recurly_MRR__c AS MRR, CASE WHEN A.Trial_Ends_At__c IS NOT NULL THEN 'Trial' ELSE '' END AS Trial, 
             CASE WHEN T0.[0] IS NULL AND t0.[10] IS NOT NULL THEN t0.[10] WHEN T0.[0] IS NULL AND t0.[20] IS NOT NULL THEN t0.[20] WHEN T0.[0] IS NULL AND t0.[30] IS NOT NULL THEN t0.[30] WHEN T0.[0] IS NULL AND t0.[40] IS NOT NULL THEN t0.[40] WHEN T0.[0] IS NULL AND 
             t0.[50] IS NOT NULL THEN t0.[50] WHEN T0.[0] IS NULL AND t0.[60] IS NOT NULL THEN t0.[60] WHEN T0.[0] IS NULL AND t0.[70] IS NOT NULL THEN t0.[70] WHEN T0.[0] IS NULL AND t0.[80] IS NOT NULL THEN t0.[80] WHEN T0.[0] IS NULL AND t0.[90] IS NOT NULL 
             THEN t0.[90] WHEN T0.[0] IS NULL AND t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[0] END AS DaysToFirstInspection, CASE WHEN T0.[10] IS NULL AND t0.[20] IS NOT NULL THEN t0.[20] WHEN T0.[10] IS NULL AND t0.[30] IS NOT NULL THEN t0.[30] WHEN T0.[10] IS NULL AND 
             t0.[40] IS NOT NULL THEN t0.[40] WHEN T0.[10] IS NULL AND t0.[50] IS NOT NULL THEN t0.[50] WHEN T0.[10] IS NULL AND t0.[60] IS NOT NULL THEN t0.[60] WHEN T0.[10] IS NULL AND t0.[70] IS NOT NULL THEN t0.[70] WHEN T0.[10] IS NULL AND t0.[80] IS NOT NULL 
             THEN t0.[80] WHEN T0.[10] IS NULL AND t0.[90] IS NOT NULL THEN t0.[90] WHEN T0.[10] IS NULL AND t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[10] END AS DaysTo10Percent, CASE WHEN T0.[20] IS NULL AND t0.[30] IS NOT NULL THEN t0.[30] WHEN T0.[20] IS NULL AND 
             t0.[40] IS NOT NULL THEN t0.[40] WHEN T0.[20] IS NULL AND t0.[50] IS NOT NULL THEN t0.[50] WHEN T0.[20] IS NULL AND t0.[60] IS NOT NULL THEN t0.[60] WHEN T0.[20] IS NULL AND t0.[70] IS NOT NULL THEN t0.[70] WHEN T0.[20] IS NULL AND t0.[80] IS NOT NULL 
             THEN t0.[80] WHEN T0.[20] IS NULL AND t0.[90] IS NOT NULL THEN t0.[90] WHEN T0.[20] IS NULL AND t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[20] END AS DaysTo20Percent, CASE WHEN T0.[30] IS NULL AND t0.[40] IS NOT NULL THEN t0.[40] WHEN T0.[30] IS NULL AND 
             t0.[50] IS NOT NULL THEN t0.[50] WHEN T0.[30] IS NULL AND t0.[60] IS NOT NULL THEN t0.[60] WHEN T0.[30] IS NULL AND t0.[70] IS NOT NULL THEN t0.[70] WHEN T0.[30] IS NULL AND t0.[80] IS NOT NULL THEN t0.[80] WHEN T0.[30] IS NULL AND t0.[90] IS NOT NULL 
             THEN t0.[90] WHEN T0.[30] IS NULL AND t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[30] END AS DaysTo30Percent, CASE WHEN T0.[40] IS NULL AND t0.[50] IS NOT NULL THEN t0.[50] WHEN T0.[40] IS NULL AND t0.[60] IS NOT NULL THEN t0.[60] WHEN T0.[40] IS NULL AND 
             t0.[70] IS NOT NULL THEN t0.[70] WHEN T0.[40] IS NULL AND t0.[80] IS NOT NULL THEN t0.[80] WHEN T0.[40] IS NULL AND t0.[90] IS NOT NULL THEN t0.[90] WHEN T0.[40] IS NULL AND t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[40] END AS DaysTo40Percent, 
             CASE WHEN T0.[50] IS NULL AND t0.[60] IS NOT NULL THEN t0.[60] WHEN T0.[50] IS NULL AND t0.[70] IS NOT NULL THEN t0.[70] WHEN T0.[50] IS NULL AND t0.[80] IS NOT NULL THEN t0.[80] WHEN T0.[50] IS NULL AND t0.[90] IS NOT NULL THEN t0.[90] WHEN T0.[50] IS NULL AND 
             t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[50] END AS DaysTo50Percent, CASE WHEN T0.[60] IS NULL AND t0.[70] IS NOT NULL THEN t0.[70] WHEN T0.[60] IS NULL AND t0.[80] IS NOT NULL THEN t0.[80] WHEN T0.[60] IS NULL AND t0.[90] IS NOT NULL 
             THEN t0.[90] WHEN T0.[60] IS NULL AND t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[60] END AS DaysTo60Percent, CASE WHEN T0.[70] IS NULL AND t0.[80] IS NOT NULL THEN t0.[80] WHEN T0.[70] IS NULL AND t0.[90] IS NOT NULL THEN t0.[90] WHEN T0.[70] IS NULL AND 
             t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[70] END AS DaysTo70Percent, CASE WHEN T0.[80] IS NULL AND t0.[90] IS NOT NULL THEN t0.[90] WHEN T0.[80] IS NULL AND t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[80] END AS DaysTo80Percent, CASE WHEN T0.[90] IS NULL AND 
             t0.[100] IS NOT NULL THEN t0.[100] ELSE T0.[90] END AS DaysTo90Percent, T0.[100] AS DaysTo100Percent
FROM   [dbo].[CDPGetVehicleInspectingEngagementStats] AS T0 INNER JOIN
             Salesforce.dbo.Account AS A ON T0.salesforce_account_id = A.Id INNER JOIN
             Salesforce.dbo.[User] AS u ON A.OwnerId = u.Id LEFT OUTER JOIN
             Salesforce.dbo.[User] AS u1 ON A.Onboarding_Specialist_UserLook__c = u1.Id
WHERE (A.pISU__c >= DATEADD(MONTH, - 12, GETDATE())) AND (A.Type = 'Customer')